import React, { useState } from 'react'
import styled from './Square.module.css'

const Square = () => {
    const [value, setValue] = useState([Array(9).fill(null)])

    const handleValue = () => {
        if (value === '') {
            setValue('x')
        } else if (value === 'x') {
            setValue('o')
        } else {
            setValue('x')
        }
    }

    return (
        <React.Fragment>
            <button className={styled.square} onClick={handleValue}>{value}</button>
        </React.Fragment>
    )
}

export default Square
