import Square from "./Square"
import { useState } from "react"
const Table = () => {
    const [table, setTable] = useState(Array(9).fill(null))
    const [player, setPlayer] = useState('x')
    const markSquare = (square) => {
        setTable(table.map((val, index) => {
            if (square == index && val == null) {
                return player
            }
            return val
        }))
    }
    return (
        <div>
            <div>
                <Square value={table[0]} onClick={() => { markSquare(0) }} />
                <Square value={table[1]} onClick={() => { markSquare(1) }} />
                <Square value={table[2]} onClick={() => { markSquare(2) }} />
            </div>
            <div>
                <Square value={table[3]} onClick={() => { markSquare(3) }} />
                <Square value={table[4]} onClick={() => { markSquare(4) }} />
                <Square value={table[5]} onClick={() => { markSquare(5) }} />
            </div>
            <div>
                <Square value={table[6]} onClick={() => { markSquare(6) }} />
                <Square value={table[7]} onClick={() => { markSquare(7) }} />
                <Square value={table[8]} onClick={() => { markSquare(8) }} />
            </div>
        </div>
    )
}

export default Table
