import Square from "./Square"
import { useState } from "react"
import styled from './Table.module.css'
import checkWinner from "./checkWinner"
import checkPlayer from "./checkPlayer"
import markedTable from "./markedTable"
import playerTurn from "./playerTurn"
import React from "react"
const Table = () => {
    const [table, setTable] = useState(Array(9).fill(''))
    const [player, setPlayer] = useState('X')
    const [turn, setTurn] = useState(0)

    const markSquare = (square) => {
        if (table[square] === '') {
            setPlayer(checkPlayer.player(turn))
            setTable(markedTable.updatedTable(table, square, player))
            setTurn(playerTurn.player(turn, table, square))
        }
    }

    const handleReset = () => {
        setTable(Array(9).fill(''))
        setTurn(0)
        setPlayer('X')
    }
    const winner = checkWinner.checkLines(table)

    const board = table.map((_, indx) => {
        const index = indx + 1
        if (index === 0 || index === 3 || index === 6) {
            <div className={styled.row}>
                <Square value={table[index]} onClick={() => { markSquare(index) }} />
                <Square value={table[index + 1]} onClick={() => { markSquare(index + 1) }} />
                <Square value={table[index + 2]} onClick={() => { markSquare(index + 2) }} />
            </div>
        }
    })
    return (
        <div>
            <div>
                {winner ? <h2>Winner: {winner}</h2> : <h2>Next turn: {player}</h2>}
            </div>
            <button onClick={handleReset}>Reset</button>

            <div className={styled.board}>
                {board}
            </div>
        </div>
    )
}

export default Table
