import Square from "./Square"
import { useState } from "react"
import styled from './Table.module.css'
import checkWinner from "./checkWinner"
import checkPlayer from "./checkPlayer"
import markedTable from "./markedTable"
import playerTurn from "./playerTurn"
import React from "react"
const Table = () => {
    const [table, setTable] = useState(Array(9).fill(''))
    const [player, setPlayer] = useState('X')
    const [turn, setTurn] = useState(0)
    console.log(turn);
    console.log(player);
    console.log(table);
    const markSquare = (square) => {
        if (table[square] === '') {
            setPlayer(checkPlayer.player(turn))
            setTable(markedTable.updatedTable(table, square, player))
            setTurn(playerTurn.player(turn, table, square))
        }
    }


    const handleReset = () => {
        setTable(Array(9).fill(''))
        setTurn(0)
        setPlayer('X')
    }
    const winner = checkWinner.checkLines(table)

    // const board = table.map((_, indx) => {
    //     const index = indx + 1
    //     if (index % 3 === 0) {
    //         return (<React.Fragment><Square value={table[indx]} onClick={() => { markSquare(indx) }} /><br /></React.Fragment>)
    //     } else if (index === 0 || index % 3 !== 0) {
    //         return (<Square value={table[indx]} onClick={() => { markSquare(indx) }} />)
    //     }
    // })
    return (
        <div>
            <div>
                {winner ? <h2>Winner: {winner}</h2> : <h2>Next turn: {player}</h2>}
            </div>
            <button onClick={handleReset}>Reset</button>

            <div className={styled.board}>
                <div className={styled.row}>
                    <Square value={table[0]} onClick={() => { markSquare(0) }} />
                    <Square value={table[1]} onClick={() => { markSquare(1) }} />
                    <Square value={table[2]} onClick={() => { markSquare(2) }} />
                </div>
                <div className={styled.row}>
                    <Square value={table[3]} onClick={() => { markSquare(3) }} />
                    <Square value={table[4]} onClick={() => { markSquare(4) }} />
                    <Square value={table[5]} onClick={() => { markSquare(5) }} />
                </div>
                <div className={styled.row}>
                    <Square value={table[6]} onClick={() => { markSquare(6) }} />
                    <Square value={table[7]} onClick={() => { markSquare(7) }} />
                    <Square value={table[8]} onClick={() => { markSquare(8) }} />
                </div>
            </div>
        </div>
    )
}

export default Table
