import Square from "./Square"
import { useState } from "react"
import styled from './Table.module.css'
const Table = () => {
    const [table, setTable] = useState(Array(9).fill(null))
    const [player, setPlayer] = useState('x')
    const [turn, setTurn] = useState(1)

    const markSquare = (square) => {
        setTurn(prev => prev + 1)
        if (turn % 2 !== 0) {
            setPlayer('o')
        } else {
            setPlayer('x')
        }
        setTable(table.map((val, index) => {
            if (square === index && val === null) {
                return player
            }
            return val
        }))
    }

    const checkLines = () => {
        const lines = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [0, 3, 6],
            [1, 4, 7],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6],
        ];
        for (let i = 0; i < lines.length; i++) {
            const [a, b, c] = lines[i]
            if (table[a] && table[a] === table[b] && table[a] === table[c]) {
                return table[a]
            }
        }
        return null
    }
    const handleReset = () => {
        setTable(Array(9).fill(null))
    }
    const winner = checkLines()
    return (
        <div>
            <div>
                {winner ? <h2>Winner: {winner}</h2> : <h2>Next turn: {player}</h2>}
            </div>
            {winner || turn === 9 ? <button onClick={handleReset}>Reset</button> : null}
            <div className={styled.row}>
                <Square value={table[0]} onClick={() => { markSquare(0) }} />
                <Square value={table[1]} onClick={() => { markSquare(1) }} />
                <Square value={table[2]} onClick={() => { markSquare(2) }} />
            </div>
            <div className={styled.row}>
                <Square value={table[3]} onClick={() => { markSquare(3) }} />
                <Square value={table[4]} onClick={() => { markSquare(4) }} />
                <Square value={table[5]} onClick={() => { markSquare(5) }} />
            </div>
            <div className={styled.row}>
                <Square value={table[6]} onClick={() => { markSquare(6) }} />
                <Square value={table[7]} onClick={() => { markSquare(7) }} />
                <Square value={table[8]} onClick={() => { markSquare(8) }} />
            </div>
        </div>
    )
}

export default Table
