import Square from "./Square"
import { useState } from "react"
const Table = () => {
    const [value, setValue] = useState([Array(9).fill(null)])

    const markSquare = () => {
        if (value === '') {
            setValue('x')
        } else if (value === 'x') {
            setValue('o')
        } else {
            setValue('x')
        }
    }
    return (
        <div>
            <div>
                <Square value={value[0]} onClick={markSquare} />
                <Square value={value[1]} onClick={markSquare} />
                <Square value={value[2]} onClick={markSquare} />
            </div>
            <div>
                <Square value={value[3]} onClick={markSquare} />
                <Square value={value[4]} onClick={markSquare} />
                <Square value={value[5]} onClick={markSquare} />
            </div>
            <div>
                <Square value={value[6]} onClick={markSquare} />
                <Square value={value[7]} onClick={markSquare} />
                <Square value={value[8]} onClick={markSquare} />
            </div>
        </div>
    )
}

export default Table
